import React, { Component } from 'react'
import Button from '@material-ui/core/Button';
import Input from '@material-ui/core/Input';
import FormControl from '@material-ui/core/FormControl';
class Search extends Component {
 constructor(){
   super();
   this.state = {users:[]}
 }

 handleInputChange = (event) => {
  event.preventDefault();
 let userArray = this.state.users;
 let user = event.target.user.user.value;
 userArray.push(event.target.user.value)
localStorage.setItem("users",JSON.stringify(userArray));
console.log(user);
return this.SearchInGithub(user);

 }

 SearchInGithub(user){
   console.log(user);
 return fetch(`https://https://api.github.com/search/users?q=${user}`, {
    method: 'get',
}).then((res)=>console.log(res))
.then((res)=>window.alert(res))
}


componentDidMount (){
  let user =  localStorage.getItem('users')
  let userArray = user? JSON.parse(user) : [];
  this.setState({ users :userArray })

} 
 render() {
   return (
     <FormControl onSubmit= {this.handleInputChange} >
       <Input 
        name = "user"
         placeholder="Search for..."
       /> 
       <Button variant="contained" color="primary" size ="small" type ="submit"> Search</Button>
     </FormControl>
   )
 }
}

export default Search
