import React, { Component } from 'react' ;
import { withRouter } from 'react-router'
import { Button } from '@material-ui/core';

class ListItems extends Component {
    constructor(props){
     super(props)
     this.state = {users:[]}
     this.handleclick = this.handleclick.bind(this);
    }
    handleclick (user){
        let userArray = this.state.users;
        userArray.push(user);
        this.setState({user:userArray})
        localStorage.setItem("users",JSON.stringify(userArray));
      //  this.props.history.push('/Searcheduser')
    }
    componentDidMount (){
  let user =  localStorage.getItem('users')
  let userArray = user? JSON.parse(user) : [];
  this.setState({ users :userArray })

} 
    render (){
        let lists = this.props.lists;
        const listItems = lists.map((item) =>

  <li key = {item.login}  value ={item.login} onClick = {() =>this.handleclick(item)}>{item.login} </li>   
       );

        return (
            <ul>
             {listItems}
            </ul>
        );
    }

    }
    export default ListItems;
