import React, { Component } from "react";
// import  '../const';
// import { host } from "../const";
import constant from '../constatnt'

import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import classnames from 'classnames';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import red from '@material-ui/core/colors/red';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';

const styles = theme => ({

  root: {
  //   flexGrow: 1,
      height: '100vh',
      backgroundColor: '#c8d6e5',
      overflow: 'hidden'
  },
  grow: {
    flexGrow: 1,
  },
  menuButton: {
    marginLeft: -12,
    marginRight: 20,
  },
  bar:{
    backgroundColor: '#222f3e',
},
card: {
  // maxWidth: 400,
  maxHeight: '400px'
},
media: {
  height: 0,
  // paddingTop: '56.25%', // 16:9
},
actions: {
  display: 'flex',
},
expand: {
  transform: 'rotate(0deg)',
  marginLeft: 'auto',
  transition: theme.transitions.create('transform', {
    duration: theme.transitions.duration.shortest,
  }),
},
expandOpen: {
  transform: 'rotate(180deg)',
},
avatar: {
  backgroundColor: red[500],
},
});
class RepoInfo extends Component {
  constructor(props) {
    super(props);
    this.state = { repoinfo: {}, expanded: false };
    // this.handleclick = this.handleclick.bind(this);
  }
  componentWillMount() {
   let user = this.props.match.params.user;
   let repo = this.props.match.params.repo;
   fetch(`https://api.github.com/repos/${user}/${repo}`, {
            method: 'get',
        }).then((res)=>res.json() )
        .then((res => this.setState({repoinfo:res})))
        }

        handleExpandClick = () => {
          this.setState(state => ({ expanded: !state.expanded }));
        };



  render() {
    const { classes } = this.props;
    console.log(this.state.repoinfo)
  // if (this.state.repoinfo.name != {}){
  //   return <div>
  //        <h1>{this.state.repoinfo.name}</h1>
  //        <div>this repo is created on 
  //        <span>{this.state.repoinfo.created_at}</span> 
  //        and updated at
  //        <span>          {this.state.repoinfo.updated_at} </span></div>
  //        <h1>description</h1>
  //        <p>{this.state.repoinfo.description}</p>
  //        <p>download the project from {this.state.repoinfo.downloads_url}</p>
  //   </div>
  // }
  //   return <p>error finding the repo</p>
  return(
    <div className={classes.root}>
    <AppBar position="static" className={classes.bar}>
      <Toolbar>
        <IconButton className={classes.menuButton} color="inherit" aria-label="Menu">

        </IconButton>
        <Typography variant="h6" color="inherit" className={classes.grow}>
          News
        </Typography>
        <Button color="inherit">Home</Button>
      </Toolbar>
    </AppBar>
    <Card className={classes.card}>
        <CardHeader
          avatar={
            <Avatar aria-label="Recipe" className={classes.avatar}>
              R
            </Avatar>
          }
          action={
            <IconButton>
              <MoreVertIcon />

            </IconButton>
          }
          title="Shrimp and Chorizo Paella"
          subheader="September 14, 2016"
        />
        <CardContent>
          <Typography component="p">
            This impressive paella is a perfect party dish and a fun meal to cook together with your
            guests. Add 1 cup of frozen peas along with the mussels, if you like.
          </Typography>
        </CardContent>
        <CardActions className={classes.actions} disableActionSpacing>
          <IconButton aria-label="Add to favorites">
            <FavoriteIcon />
          </IconButton>
          <IconButton aria-label="Share">
            <ShareIcon />
          </IconButton>
          <IconButton
            className={classnames(classes.expand, {
              [classes.expandOpen]: this.state.expanded,
            })}
            onClick={this.handleExpandClick}
            aria-expanded={this.state.expanded}
            aria-label="Show more"
          >
            <ExpandMoreIcon />
          </IconButton>
        </CardActions>
        <Collapse in={this.state.expanded} timeout="auto" unmountOnExit>
          <CardContent>
            <Typography paragraph>Method:</Typography>
            <Typography paragraph>
              Heat 1/2 cup of the broth in a pot until simmering, add saffron and set aside for 10
              minutes.
            </Typography>
            <Typography paragraph>
              Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over medium-high
              heat. Add chicken, shrimp and chorizo, and cook, stirring occasionally until lightly
              browned, 6 to 8 minutes. Transfer shrimp to a large plate and set aside, leaving
              chicken and chorizo in the pan. Add pimentón, bay leaves, garlic, tomatoes, onion,
              salt and pepper, and cook, stirring often until thickened and fragrant, about 10
              minutes. Add saffron broth and remaining 4 1/2 cups chicken broth; bring to a boil.
            </Typography>
            <Typography paragraph>
              Add rice and stir very gently to distribute. Top with artichokes and peppers, and cook
              without stirring, until most of the liquid is absorbed, 15 to 18 minutes. Reduce heat
              to medium-low, add reserved shrimp and mussels, tucking them down into the rice, and
              cook again without stirring, until mussels have opened and rice is just tender, 5 to 7
              minutes more. (Discard any mussels that don’t open.)
            </Typography>
            <Typography>
              Set aside off of the heat to let rest for 10 minutes, and then serve.
            </Typography>
          </CardContent>
        </Collapse>
      </Card>
    </div>
  )
  }
}

RepoInfo.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(RepoInfo);
