import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

const styles = {
  root: {
  //   flexGrow: 1,
      height: '100vh',
      backgroundColor: '#c8d6e5',
      overflow: 'hidden'
  },
  grow: {
    flexGrow: 1,
  },
  menuButton: {
    marginLeft: -12,
    marginRight: 20,
  }
};
class RepoInfo extends Component {
  constructor(props) {
    super(props);
    this.state = { repoinfo: {} };
    // this.handleclick = this.handleclick.bind(this);
  }
  componentWillMount() {
   let user = this.props.match.params.user;
   let repo = this.props.match.params.repo;
   fetch(`https://api.github.com/repos/${user}/${repo}`, {
            method: 'get',
        }).then((res)=>res.json() )
        .then((res => this.setState({repoinfo:res})))
        }

  render() {
    const { classes } = this.props;
    console.log(this.state.repoinfo)
  // if (this.state.repoinfo.name != {}){
  //   return <div>
  //        <h1>{this.state.repoinfo.name}</h1>
  //        <div>this repo is created on 
  //        <span>{this.state.repoinfo.created_at}</span> 
  //        and updated at
  //        <span>          {this.state.repoinfo.updated_at} </span></div>
  //        <h1>description</h1>
  //        <p>{this.state.repoinfo.description}</p>
  //        <p>download the project from {this.state.repoinfo.downloads_url}</p>
  //   </div>
  // }
  //   return <p>error finding the repo</p>
  retrun(
    <div className={classes.root}>
    <AppBar position="static" className={classes.bar}>
      <Toolbar>
        <IconButton className={classes.menuButton} color="inherit" aria-label="Menu">

        </IconButton>
        <Typography variant="h6" color="inherit" className={classes.grow}>
          News
        </Typography>
        <Button color="inherit">Home</Button>
      </Toolbar>
    </AppBar>
    </div>
  )
  }
}

RepoInfo.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Repo);
