import React, { Component } from 'react'
import Button from '@material-ui/core/Button';
import Input from '@material-ui/core/Input';
import FormControl from '@material-ui/core/FormControl';
class Search extends Component {
 constructor(){
   super();
   this.state = {users:[]}
   this.handleInputChange = this.handleInputChange.bind(this);
 }

 handleInputChange  (event) {
  alert(event.target.user.value);
  event.preventDefault();
 let userArray = this.state.users;
 let user = event.target.user.value;
 userArray.push(event.target.user.value)
localStorage.setItem("users",JSON.stringify(userArray));
console.log(user);
return this.SearchInGithub(user);

 }

 SearchInGithub(user){
   console.log(user);
 return fetch(`https://api.github.com/search/users?q=mayankmishra12`, {
    method: 'get',
}).then((res)=>res.json() )
.then((res)=>console.log(res.items[0]))
}


componentDidMount (){
  let user =  localStorage.getItem('users')
  let userArray = user? JSON.parse(user) : [];
  this.setState({ users :userArray })

} 
 render() {
   return (
     <form onSubmit= {this.handleInputChange} >
       <Input 
        name = "user"
         placeholder="Search for..."
       /> 
       <Button variant="contained" color="primary" size ="small" type ="submit"> Search</Button>
     </form>
   )
 }
}

export default Search
